def canMove(maze, direction, x, y): 
    if (y < 0 or y > len(maze) or x < 0 or x > len(maze[y])):
        return False
    try:
        if (direction == "down"  and maze[y+1][x] != " " or \
            direction == "up"    and maze[y-1][x] != " " or \
            direction == "left"  and maze[y][x-1] != " " or \
            direction == "right" and maze[y][x+1] != " "): 
            return True
        return False
    except IndexError:
        return False    

def move(direction, cX, cY): 
    if (direction == "down"):
        return cX, cY+1
    elif (direction == "up"):
        return cX, cY-1
    elif (direction == "left"):
        return cX-1, cY
    elif (direction == "right"):
        return cX+1, cY
    else:
        print("FATAL ERROR! Move was given direction", direction)
        return -1, -1

def printMap(maze, x, y):
    string = ""
    for i in range(len(maze)):
        for j in range(len(maze[i])):
            if (i == y and j == x):
                string += '@'
            else:
                string += maze[i][j]
        string += '\n'
    print(string)

def directionChange(maze, direction, x, y):
    if (direction == "down" or direction == "up"):
        if (maze[y][x-1] != ' '):
            return "left"
        elif (maze[y][x+1] != ' '):
            return "right"
        else:
            return ""
    elif (direction == "left" or direction == "right"):
        if (maze[y-1][x] != ' '):
            return "up"
        elif (maze[y+1][x] != ' '):
            return "down"
        else:
            return ""
    else:
        print("FATAL ERROR! directionChange was given direction", direction)
        return "ERROR"

