#maze = ["     |          ","     |  +--+    ","     A  |  C    "," F---|----E|--+ ","     |  |  |  D ","     +B-+  +--+ "]

cX, cY = maze[0].index('|'), 0
direction = "down"
checkpoints = ""


counter = 0
while (True): 
    # Usual operations at every step. 
    print("Step", counter)
    #printMap(maze, cX, cY)
    counter += 1
    if (ord(maze[cY][cX]) > 64 and ord(maze[cY][cX]) < 123):
        checkpoints += maze[cY][cX]
    
    # If they hit a wall (can't move), change direction before the moving
    if (not canMove(maze, direction, cX, cY)):
        direction = directionChange(maze, direction, cX, cY)
        
        # Extra special case of being at the end!
        if (direction == ""):
            break
        
    # Take a distance jump in the direction that they are moving. 
    cX, cY = move(direction, cX, cY)
    
        
    
print("Checkpoints passed were:", checkpoints)